import React from 'react';
import css from './ContactList.module.css';
import { useDispatch } from 'react-redux';
import { deleteItem } from '../../redux/contactSlice';

export const ContactList = ({ visibleContacts }) => {
  const dispatch = useDispatch();

  return (
    <>
      <h2 className={css.contacts__title}>Contacts</h2>
      <div className={css.contacts}>
        <ul className={css.contacts__list}>
          {filteredContacts.map(contact => (
            <li className={css.contacts__item} key={contact.id}>
              <p className={css.contacts__name}>{contact.name}</p>
              <p className={css.contacts__number}>{contact.number}</p>
              <button
                className={css.contacts__btn}
                type="button"
                onClick={() => dispatch(deleteItem(id))}
              >
                delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};
